/**
 * Проверяет, является ли HTTP-метод «безопасным» (не изменяющим состояние ресурса).
 * Считаются безопасными: GET, HEAD, OPTIONS.
 *
 * Используется для того, чтобы:
 * 1) В кэширующем интерсепторе кэшировать только безопасные запросы (обычно GET).
 * 2) В интерсепторе инвалидации пропускать безопасные запросы и выполнять
 *    инвалидацию только для мутаций (POST/PUT/PATCH/DELETE).
 */
export function isSafeMethod(method: string): boolean {
  return ['GET', 'HEAD', 'OPTIONS'].includes(method);
}
